            compareFirstWithSecondList: function (list1, list2) {
                var found = false;
                var result = [];
                for(var i=0;i<list2.length;i++){
                    found = false;
                    for(var j=0;j<list1.length;j++){
                        if(list2[i].split(".")[0] == list1[j].split(".")[0]){
                            found = true;
                            break;
                        }
                    }
                    if(!found){
                        result.push(list2[i].split(".")[0]);
                    }
                }
                return result;
            },
