    $scope.loadDataForBreadCrumb = function(node){
        var tliData, d, d1;
        $scope.customDateFilterApplied = false;
        $scope.dateRangeFilterName = GlobalService.getVal('customdate');
        $scope.drillDown = true;
        $scope.info.currentFacet = null;
        var currentData = node;
        var chartLevel = currentData.ts;

        tliData = currentData;
        if($scope.timelineGraphDrilDown.length == 0){
            $scope.setFromTo(tliData.from, tliData.to);
            return;
        }
        d = new Date(tliData.from);
        d1 = new Date(tliData.to);
        switch(chartLevel) {
            case 'YEAR' :
                $scope.setFromTo(d, d1);
                break;
            case 'MONTH' :
                d.setMonth(0);
                d.setDate(1);
                d.setHours(0);
                d.setMinutes(0);
                d.setSeconds(0);
                d1.setMonth(12);
                d1.setDate(0);
                d1.setHours(23);
                d1.setMinutes(59);
                d1.setSeconds(59);
                $scope.setFromTo(d, d1);
                break;
            case 'DAY' :
                d.setDate(1);
                d.setHours(0);
                d.setMinutes(0);
                d.setSeconds(0);
                d1.setMonth(d1.getMonth() + 1);
                d1.setDate(0);
                d1.setHours(23);
                d1.setMinutes(59);
                d1.setSeconds(59);
                $scope.setFromTo(d, d1);
                break;
            case 'HOUR' :
                d.setHours(0);
                d.setMinutes(0);
                d.setSeconds(0);
                d1.setHours(23);
                d1.setMinutes(59);
                d1.setSeconds(59);
                $scope.setFromTo(d, d1);
                break;
            case 'MINUTES' :
                d.setMinutes(0);
                d.setSeconds(0);
                d1.setMinutes(59);
                d1.setSeconds(59);
                $scope.setFromTo(d, d1);
                break;
        }        
    }


