    $scope.isDuplicateMostRecentSearch = function(val) {
        if (!$scope.info.savedFilters || !$scope.info.savedFilters.length)
            return false;
        var len = $scope.info.savedFilters.length;
        for (var i = 0; i < len; i++) {
            if (val == $scope.info.savedFilters[i]) {
                return true;
            }
        }

        return false;
    };
    
